CREATE EXTENSION IF NOT EXISTS "uuid-ossp";
CREATE TABLE "tariffs" ("id" SERIAL NOT NULL, "name" character varying NOT NULL, "price" numeric NOT NULL, "duration_days" integer, "features" jsonb, "is_active" boolean NOT NULL DEFAULT true, CONSTRAINT "PK_7f32baf8d8b4bb0cf4d7ac97741" PRIMARY KEY ("id"));
CREATE TYPE "users_role_enum" AS ENUM('company', 'client');
CREATE TYPE "users_status_enum" AS ENUM('pending', 'active');
CREATE TABLE "users" ("id" uuid NOT NULL DEFAULT uuid_generate_v4(), "role" "users_role_enum" NOT NULL DEFAULT 'company', "name" character varying NOT NULL, "email" character varying NOT NULL, "password" character varying NOT NULL, "status" "users_status_enum" NOT NULL DEFAULT 'pending', "refreshTokenHash" character varying, "tariffStartAt" TIMESTAMP, "tariffEndAt" TIMESTAMP, "tariffId" integer, CONSTRAINT "UQ_97672ac88f789774dd47f7c8be3" UNIQUE ("email"), CONSTRAINT "PK_a3ffb1c0c8416b9fc6f907b7433" PRIMARY KEY ("id"));
CREATE TYPE "verification_sessions_status_enum" AS ENUM('pending', 'locked', 'used', 'expired');
CREATE TABLE "verification_sessions" ("id" uuid NOT NULL DEFAULT uuid_generate_v4(), "userId" uuid NOT NULL, "codeHash" character varying NOT NULL, "expiresAt" TIMESTAMP WITH TIME ZONE NOT NULL, "attemptsLeft" integer NOT NULL DEFAULT '5', "status" "verification_sessions_status_enum" NOT NULL DEFAULT 'pending', "lastSentAt" TIMESTAMP WITH TIME ZONE, "resendCount" integer NOT NULL DEFAULT '0', "createdAt" TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT now(), "updatedAt" TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT now(), CONSTRAINT "PK_5e6a11608a05c9d913348fe8f77" PRIMARY KEY ("id"));
CREATE INDEX "IDX_b9e1188df462b24eaffc4d0256" ON "verification_sessions" ("userId") ;
CREATE TABLE "articles" ("id" SERIAL NOT NULL, "type" character varying NOT NULL, "title" character varying NOT NULL, "slug" character varying, "content" character varying NOT NULL, "image_url" character varying, "authorCompanyId" integer, CONSTRAINT "PK_0a6e2c450d83e0b6052c2793334" PRIMARY KEY ("id"));
CREATE TABLE "ads" ("id" SERIAL NOT NULL, "position" character varying NOT NULL, "image_url" character varying, "link" character varying, "start_date" date, "end_date" date, "is_active" boolean NOT NULL DEFAULT true, "companyCompanyId" integer, CONSTRAINT "PK_a7af7d1998037a97076f758fc23" PRIMARY KEY ("id"));
CREATE TYPE "blogs_status_enum" AS ENUM('draft', 'published');
CREATE TABLE "blogs" ("id" uuid NOT NULL DEFAULT uuid_generate_v4(), "company_id" integer NOT NULL, "title" character varying NOT NULL, "description" text NOT NULL, "image_url" character varying NOT NULL, "content_blocks" jsonb, "status" "blogs_status_enum" NOT NULL DEFAULT 'draft', "is_pinned" boolean NOT NULL DEFAULT false, "created_at" TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT now(), CONSTRAINT "PK_e113335f11c926da929a625f118" PRIMARY KEY ("id"));
CREATE TABLE "companies" ("companyId" SERIAL NOT NULL, "user_id" uuid, "logo_url" character varying, "name" character varying NOT NULL, "description" character varying, "regions" jsonb, "social_links" jsonb, "address" character varying, "phones" jsonb, "emails" jsonb, "email" character varying, "created_at" TIMESTAMP NOT NULL DEFAULT now(), "updated_at" TIMESTAMP NOT NULL DEFAULT now(), CONSTRAINT "UQ_ee0839cba07cb0c52602021ad4b" UNIQUE ("user_id"), CONSTRAINT "REL_ee0839cba07cb0c52602021ad4" UNIQUE ("user_id"), CONSTRAINT "PK_9de34f59e8578db786054269261" PRIMARY KEY ("companyId"));
CREATE TABLE "orders" ("id" SERIAL NOT NULL, "company_id" integer NOT NULL, "title" character varying NOT NULL, "orderLogoUrl" character varying NOT NULL, "region" character varying NOT NULL, "price" integer NOT NULL, "category" character varying NOT NULL, "status" character varying NOT NULL, "created_at" TIMESTAMP NOT NULL DEFAULT now(), "updated_at" TIMESTAMP NOT NULL DEFAULT now(), CONSTRAINT "PK_710e2d4957aa5878dfe94e4ac2f" PRIMARY KEY ("id"));
CREATE INDEX "IDX_c98ec64a6771f8c431a81a4393" ON "orders" ("company_id", "status", "created_at") ;
CREATE TABLE "password_reset_tokens" ("id" uuid NOT NULL DEFAULT uuid_generate_v4(), "userId" uuid NOT NULL, "tokenHash" character varying NOT NULL, "expiresAt" TIMESTAMP WITH TIME ZONE NOT NULL, "usedAt" TIMESTAMP WITH TIME ZONE, "createdAt" TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT now(), CONSTRAINT "PK_d16bebd73e844c48bca50ff8d3d" PRIMARY KEY ("id"));
CREATE INDEX "IDX_d6a19d4b4f6c62dcd29daa497e" ON "password_reset_tokens" ("userId") ;
