CREATE TABLE "search_index" ("id" SERIAL NOT NULL, "entity_type" character varying NOT NULL, "entity_id" integer NOT NULL, "title" character varying, "content" character varying, "indexed_at" TIMESTAMP NOT NULL DEFAULT now(), CONSTRAINT "PK_98e7fcdd8a5c077de6d88d2be28" PRIMARY KEY ("id"));
CREATE TABLE "contractor" ("id" uuid NOT NULL DEFAULT uuid_generate_v4(), "title" character varying NOT NULL, "badges" text array NOT NULL, CONSTRAINT "UQ_5269acb88b91317fc3e8ed7c5f0" UNIQUE ("title"), CONSTRAINT "PK_27a7037ba4d95c429e611cef10e" PRIMARY KEY ("id"));
CREATE TABLE "news" ("id" uuid NOT NULL DEFAULT uuid_generate_v4(), "image" character varying NOT NULL, "imageAlt" character varying NOT NULL, "category" character varying NOT NULL, "title" character varying NOT NULL, "author" character varying NOT NULL, "description" character varying, "createdAt" TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT now(), "content" text array, "isAds" boolean NOT NULL DEFAULT false, "isImportantNew" boolean NOT NULL DEFAULT false, CONSTRAINT "PK_39a43dfcb6007180f04aff2357e" PRIMARY KEY ("id"));
CREATE TABLE "contests" ("id" SERIAL NOT NULL, "title" character varying NOT NULL, "contestLink" text NOT NULL, "startDate" date NOT NULL, "endDate" date NOT NULL, "imageUrl" character varying, CONSTRAINT "PK_0b8012f5cf6f444a52179e1227a" PRIMARY KEY ("id"));
CREATE TABLE "company_review_replies" ("id" SERIAL NOT NULL, "review_id" integer NOT NULL, "company_id" integer NOT NULL, "author_id" uuid NOT NULL, "reply_text" text NOT NULL, "created_at" TIMESTAMP NOT NULL DEFAULT now(), "updated_at" TIMESTAMP NOT NULL DEFAULT now(), CONSTRAINT "UQ_0d927d74465b645682ae35bf268" UNIQUE ("review_id"), CONSTRAINT "REL_0d927d74465b645682ae35bf26" UNIQUE ("review_id"), CONSTRAINT "PK_73854feeb03eb12845fbd89f0f7" PRIMARY KEY ("id"));
CREATE TABLE "company_reviews" ("id" SERIAL NOT NULL, "order_id" bigint NOT NULL, "company_id" integer NOT NULL, "author_id" uuid NOT NULL, "service_id" bigint, "service_name" text, "rating" smallint NOT NULL, "comment" text, "created_at" TIMESTAMP NOT NULL DEFAULT now(), "updated_at" TIMESTAMP NOT NULL DEFAULT now(), CONSTRAINT "UQ_f51c684ea75b72ac4ee49ce1434" UNIQUE ("order_id"), CONSTRAINT "PK_5495f665609ea006242464173c4" PRIMARY KEY ("id"));
CREATE TABLE "company_services" ("id" uuid NOT NULL DEFAULT uuid_generate_v4(), "company_id" integer NOT NULL, "category" character varying NOT NULL, "service" character varying NOT NULL, "category_name" character varying NOT NULL, "image_url" character varying, "created_at" TIMESTAMP NOT NULL DEFAULT now(), "updated_at" TIMESTAMP NOT NULL DEFAULT now(), CONSTRAINT "PK_25fc94c7e4cfcf84d772c76c68e" PRIMARY KEY ("id"));
CREATE INDEX "IDX_31f0b599414a1caa0ea48be363" ON "company_services" ("company_id", "category") ;
CREATE TABLE "catalog_filter" ("id" SERIAL NOT NULL, "category" character varying NOT NULL, "item" character varying NOT NULL, CONSTRAINT "PK_6b901e02474d6383f0bb5feb51c" PRIMARY KEY ("id"));
